import {
  BAD_REQUEST, CONFLICT, NOT_FOUND, SERVER_ERROR, FORBIDDEN, UNAUTHORIZED,
} from './errors';

/**
   * Handle Bad Requests errors
   *
   * @param {object} err
   * @param {object} req
   * @param {object} res
   * @param {object} next
   * @return {*}
   */
const badRequest = (err, req, res, next) => {
  if (err.status !== BAD_REQUEST) {
    next();
  }

  // Handle invalid JSON body
  if (err.type && err.type.includes('entity.parse.failed')) {
    return res.status(BAD_REQUEST).json({
      success: false,
      errors: [
        {
          message: 'Invalid JSON object check request body',
          body: err.body,
        },
      ],
    });
  }

  return res.status(BAD_REQUEST).json({
    success: false,
    errors: {
      message: err.message || 'Bad Request',
      status: err.status,
    },
  });
};

const forbidden = (err, req, res, next) => {
  if (err.status !== FORBIDDEN) {
    return next(err);
  }

  return res.status(FORBIDDEN).json({
    success: false,
    message: err.message || 'Forbidden',
    errors: [err],
  });
};

const unauthorized = (err, req, res, next) => {
  if (err.status !== UNAUTHORIZED) {
    return next(err);
  }

  return res.status(UNAUTHORIZED).json({
    success: false,
    message: err.message || 'Unauthorized',
    errors: [err],
  });
};

/**
   * Handle resource not found
   *
   * @param {object} err
   * @param {object} req
   * @param {object} res
   * @param {object} next
   * @return {object}
   */
const notFound = (err, req, res, next) => {
  if (err.status !== NOT_FOUND) {
    return next(err);
  }

  return res.status(NOT_FOUND).json({
    success: false,
    message: err.message || 'Resource not found',
    errors: [err],
  });
};

/**
   * Handle conflict errors for resources that already exist
   *
   * @param {object} err
   * @param {object} req
   * @param {object} res
   * @param {object} next
   * @return {object}
   */
const resourceConflict = (err, req, res, next) => {
  if (err.status !== CONFLICT) {
    return next(err);
  }

  return res.status(CONFLICT).json({
    success: false,
    errors: {
      message: err.message,
      status: err.status,
    },
  });
};

/**
   * Handle server error
   *
   * @param {object} err
   * @param {object} req
   * @param {object} res
   * @param {object} next
   * @return {object}
   */
const serverError = (err, req, res, next) => res.status(SERVER_ERROR).json({
  success: false,
  message: err.message || 'Internal server error',
  errors: [err],
});
/**
   * Package all error handlers as object
   */
const errorsObject = {
  badRequest,
  notFound,
  resourceConflict,
  forbidden,
  unauthorized,
  serverError,
};

/**
   * Export all error middleware as an array
   *
   */
const customErrorHandler = () => Object.keys(errorsObject).map((key) => errorsObject[key]);

export default customErrorHandler;

